(function(e){e.fn.idScroller=function(t){var n=e.extend({speed:1e3,pause:2e3,hoverPause:true,infiniteLoop:true},t);return this.each(function(){function o(){var i=parseInt(t.css("marginTop"));var s=t.find(".idScrollerItem.active").first();var o=parseInt(s.outerHeight());if(!n.infiniteLoop){o-=i}r.removeClass("active");if(!n.infiniteLoop){if(s.hasClass("last")){t.stop(true,false).animate({marginTop:"0"},n.speed);t.find(".idScrollerItem.first").addClass("active");return false}else{t.stop(true,false).animate({marginTop:"-"+o+"px"},n.speed);s.next(".idScrollerItem").addClass("active")}}else{s.clone().appendTo(t).removeClass("first").removeClass("active").addClass("last");t.stop(true,false).animate({marginTop:"-"+o+"px"},n.speed,function(){s.next(".idScrollerItem").addClass("active").addClass("first");t.css("marginTop",0);e(".idScrollerItem",t).removeClass("last");s.remove()})}}var t=e(this),r=t.children("div"),i=0;t.parent().addClass("idScrollerContainer");var s=e(".idScrollerContainer").height();r.addClass("idScrollerItem");r.first().addClass("first");r.last().addClass("last");r.each(function(t,n){i+=e(this).outerHeight()});if(i>s){var u=setInterval(o,n.pause)}if(n.hoverPause){t.mouseenter(function(){clearInterval(u)});t.mouseleave(function(){u=setInterval(o,n.pause)})}})}})(jQuery)